import React, { useState } from 'react';
import './UserHomePage.css'; // Import CSS file for styling
import Modal from './Modal'; // Import Modal component for user details form

const UserHomePage = () => {
  // State to manage user details modal
  const [showModal, setShowModal] = useState(false);

  // Function to toggle user details modal
  const toggleModal = () => {
    setShowModal(!showModal);
  };

  // Dummy data for all policies
  const allPolicies = [
    { name: 'Policy 1', coverageAmount: '$1,000,000', premium: '$100/month' },
    { name: 'Policy 2', coverageAmount: '$500,000', premium: '$50/month' },
    { name: 'Policy 3', coverageAmount: '$750,000', premium: '$75/month' }
  ];

  // Function to handle buying a policy
  const buyPolicy = () => {
    // Logic to buy policy
    // Change button color to gray
  };

  return (
    <div className="user-home-page">
      {/* Header Area */}
      <header className="header">
        <div className="logo">
          <img src="/assets/name.png" alt="Logo" />
        </div>
        <div className="title">Home | Ragnee</div>
        <div className="user-profile" onClick={toggleModal}>
          <img src="profile-picture.png" alt="User Profile" />
        </div>
      </header>

      {/* Navigation Sidebar */}
      <nav className="sidebar">
        <ul>
          <li>Make Claim</li>
          <li>Claim History</li>
          <li>My Policies</li>
        </ul>
      </nav>

      {/* Main Content Area */}
      <main className="main-content">
        {/* All Policies */}
        <section className="all-policies">
          <h2>All Policies</h2>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Coverage Amount</th>
                <th>Premium</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {allPolicies.map((policy, index) => (
                <tr key={index}>
                  <td>{policy.name}</td>
                  <td>{policy.coverageAmount}</td>
                  <td>{policy.premium}</td>
                  <td>
                    <button className="buy-button" onClick={buyPolicy}>Buy</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </main>

      {/* User Details Section */}
      <aside className="user-details">
        <h2>User Details</h2>
        <ul>
          <li>Contact Details: user@example.com</li>
          {/* Add more user details as needed */}
        </ul>
      </aside>

      {/* User Details Modal */}
      {showModal && <Modal onClose={toggleModal} />}
    </div>
  );
};

export default UserHomePage;
