import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Userhome.css';
import MyPoliciesModal from './MyPoliciesModal';

const UserDetails = ({ userDetails }) => {
  return (
    <div>
      <h2>User Details</h2>
      <ul>
        <li>Name: {userDetails.name}</li>
        <li>Email: {userDetails.email}</li>
        <li>Contact: {userDetails.contact}</li>
        {/* Add more user details as needed */}
      </ul>
    </div>
  );
};

const UserHomePage = () => {
  const [policies, setPolicies] = useState([]);
  const [myPolicies, setMyPolicies] = useState([]);
  const [userDetails, setUserDetails] = useState({});

  useEffect(() => {
    axios.get('http://localhost:8080/user/policy')
      .then(response => {
        setPolicies(response?.data?.policies);
      })
      .catch(err => console.log(err));

    // Fetch user details (replace with actual API endpoint)
    axios.get('http://localhost:8080/user/details')
      .then(response => {
        setUserDetails(response?.data);
      })
      .catch(err => console.log(err));
  }, []);

  const handleAddToMyPolicies = (policyId) => {
    const policyToAdd = policies.find(policy => policy.policyId === policyId);
    if (policyToAdd) {
      setMyPolicies(prevPolicies => [...prevPolicies, policyToAdd]);
    }
  };

  return (
    <div>
      {/* Navigation Bar */}
      <nav className="navigation">
        <ul>
          <li>My Policies</li>
          <li>Make Claim</li>
          <li>History</li>
        </ul>
      </nav>

      {/* User Details Section */}
      <UserDetails userDetails={userDetails} />

      {/* Display All Policies */}
      <h2>All Policies</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Coverage Amount</th>
            <th>Premium Amount</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {policies.map(policy => (
            <tr key={policy.policyId}>
              <td>{policy.name}</td>
              <td>{policy.coverageAmount}</td>
              <td>{policy.premiumAmount}</td>
              <td>
                <button
                  className="add-button"
                  onClick={() => handleAddToMyPolicies(policy.policyId)}
                  disabled={myPolicies.some(p => p.policyId === policy.policyId)}
                >
                  {myPolicies.some(p => p.policyId === policy.policyId) ? 'Added' : 'Add to My Policies'}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Display My Policies Modal */}
      <MyPoliciesModal myPolicies={myPolicies} />
    </div>
  );
};

export default UserHomePage;
