import React,{useState} from 'react'
import './Login.css'
import email_icon from '../Assets/email.jpg'
import password_icon from '../Assets/password.png';

const Login = () => {
    const [action,setAction]=useState("Register")
   const [data,setData]=useState({
    email:"",
    password:"",
   })

const handleChange =({currentTarget:input})=>{
    setData({...data,[input.name]:input.value})
}

  return (
    <div className='container'>
        <div className='header'>
        <div className='text'>{action}</div>
        <div className='underline'></div>
        </div>
        <div className='inputs'>
            <div className='input'>
                <img src={email_icon} alt=''/>
                <input type='text' 
                placeholder='Email'
                name='email' 
                onChange={handleChange}
                value={data.email}
                required
                className='input'/>
            </div>
            <div className='input'>
                <img src={password_icon} alt=''/>
                <input type='text' 
                placeholder='Password'
                name='password' 
                onChange={handleChange}
                value={data.password}
                required
                className='input'/>
            </div>
        </div>
        <div className='submit-container'>
            <div className={action==="Login"?"submit gray":"submit"} onClick={()=>{setAction("Register")}}>Register</div>
            <div className={action==="Register"?"submit gray":"submit"}onClick={()=>{setAction("Login")}}>Login</div>
        </div>
    </div>
  )
}

module.exports={Login};
