import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Userhome.css';

const UserHomePage = () => {
  const [policies, setPolicies] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8080/user/policy')
      .then(response => {
        setPolicies(response?.data?.policies);
      })
      .catch(err => console.log(err));
  }, []);

  const handleAddToMyPolicies = (policyId) => {
    // Logic to add policy to "My Policies" goes here
    console.log(`Policy ${policyId} added to My Policies`);
  };

  return (
    <div className="user-home-page">
      {/* Navigation Bar */}
      <nav className="navigation-bar">
        <ul>
          <li>My Policies</li>
          <li>Make Claim</li>
          <li>History</li>
        </ul>
      </nav>

      {/* User Information */}
      <aside className="user-information">
        <h2>User Information</h2>
        {/* Display user information here */}
      </aside>

      {/* Policies Table */}
      <main className="policies-table">
        <h2>All Policies</h2>
        <table>
          <thead>
            <tr>
              <th>Policy ID</th>
              <th>Policy Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {policies.map(policy => (
              <tr key={policy.policyId}>
                <td>{policy.policyId}</td>
                <td>{policy.name}</td>
                <td>
                  <button
                    className="add-to-my-policies"
                    onClick={() => handleAddToMyPolicies(policy.policyId)}
                  >
                    Add to My Policies
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
};

export default UserHomePage;
