import React, { useState, useEffect } from 'react';
import './Userhome.css';
import axios from 'axios';

const UserHomePage = () => {
  const [policies, setPolicies] = useState([]);

  useEffect(() => {
    // Fetch policies from backend when component mounts
    fetchPolicies();
  }, []);

  const fetchPolicies = async () => {
    try {
      const response = await axios.get('http://localhost:8080/user/policy');
      setPolicies(response.data);
    } catch (error) {
      console.error('Error fetching policies:', error);
    }
  };

  const handleAddPolicy = async (policyId) => {
    try {
      await axios.post(`http://localhost:8080/user/policy/${policyId}`);
      // Optionally, update state to reflect the added policy
    } catch (error) {
      console.error('Error adding policy:', error);
    }
  };

  return (
    <div>
      <h1>User Home Page</h1>
      <h2>Policies</h2>
      <ul>
        {policies.map((policy) => (
          <li key={policy.id}>
            {policy.name} - {policy.description}
            <button onClick={() => handleAddPolicy(policy.id)}>Add</button>
          </li>
        ))}
      </ul>
      {/* Add section for making claims */}
    </div>
  );
};

export default UserHomePage;
