import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserHomePage.css';

const UserHomePage = () => {
  const [user, setUser] = useState({
    name: '',
    email: '',
    contact: ''
  });

  const [policies, setPolicies] = useState([]);

  useEffect(() => {
    // Fetch user details and policies on component mount
    axios.get('http://localhost:8080/user/details')
      .then(response => {
        setUser(response?.data?.user);
      })
      .catch(err => console.log(err));

    axios.get('http://localhost:8080/user/policies')
      .then(response => {
        setPolicies(response?.data?.policies);
      })
      .catch(err => console.log(err));
  }, []);

  const handleAddToMyPolicies = (policyId) => {
    // Logic to add policy to "My Policies" goes here
    console.log(`Policy ${policyId} added to My Policies`);
  };

  return (
    <div className="user-home-page">
      {/* Dashboard */}
      <aside className="dashboard">
        <h2>Dashboard</h2>
        <ul>
          <li>My Policies</li>
          <li>Make Claim</li>
          <li>History</li>
        </ul>
      </aside>

      {/* User Information */}
      <section className="user-information">
        <h2>User Information</h2>
        <div>
          <strong>Name:</strong> {user.name}<br />
          <strong>Email:</strong> {user.email}<br />
          <strong>Contact:</strong> {user.contact}<br />
        </div>
      </section>

      {/* Policies Table */}
      <main className="policies-table">
        <h2>All Policies</h2>
        <table>
          <thead>
            <tr>
              <th>Policy ID</th>
              <th>Policy Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {policies.map(policy => (
              <tr key={policy.policyId}>
                <td>{policy.policyId}</td>
                <td>{policy.name}</td>
                <td>
                  <button
                    className="add-to-my-policies"
                    onClick={() => handleAddToMyPolicies(policy.policyId)}
                  >
                    Add to My Policies
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
};

export default UserHomePage;
